// ============================================
// Function: exec_run_max
// --------------------------------------------

void mc_testbench::exec_run_max(ac_fixed<12, 8, true, AC_TRN, AC_WRAP> input[32768], ac_fixed<12, 8, true, AC_TRN, AC_WRAP> output[32768], ac_int<7, false> &channels, ac_int<7, false> &height, ac_int<7, false> &width, ac_int<3, false> &pool_size, ac_int<3, false> &stride)
{
   #ifndef CCS_SCVERIFY_USE_CCS_BLOCK
   wait_for_idle_sync();
   capture_IN(input, output, channels, height, width, pool_size, stride);
   UNET_IP::maxpool::run_max(input, output, channels, height, width, pool_size, stride);
   // throttle ac_channel based on number of calls to chan::size() or chan::empty() or chan::nb_read() (but not chan::available()) 
   if (1) {
      int cnt=0;
      if (cnt) std::cout << "mc_testbench.cpp: CONTINUES @ " << sc_time_stamp() << std::endl;
      if (cnt) that->cpp_testbench_active.write(true);
   }
   capture_OUT(input, output, channels, height, width, pool_size, stride);
   #else
   #endif
}
